SELECT fldCRN, fldFirstName, fldLastName, fldCourseName FROM tblStudents JOIN tblEnrolls ON tblEnrolls.fnkStudentId = tblStudents.pmkStudentId JOIN tblSections ON tblSections.fnkCourseId = tblEnrolls.fnkCourseId JOIN tblCourses ON tblCourses.pmkCourseId = tblSections.fnkCourseId WHERE tblCourses.fldCourseName = "Database Design for the Web" AND tblCourses.fldCourseNumber = "148" AND tblCourses.fldDepartment = "CS" GROUP BY fldFirstName, fldLastName ORDER BY fldCRN, fldLastName, fldFirstName
